import React, { useEffect } from 'react'
import { useParams, useHistory } from 'react-router-dom';
import { readDeck, updateDeck } from '../utils/api/index';

export function EditDeck({ deck, setDeck }) {
  const history = useHistory();
  const { deckId } = useParams();

    useEffect(() => {
        async function loadDecks() {
          const loadedDeck = await readDeck(deckId);
          setDeck(loadedDeck);
        }
        loadDecks();
      }, [deckId, setDeck]);



 //event change handlers on form fields including:
 //NAME
 //DESCRIPTION
 //CANCEL FUNCTION
//SAVE FUNCTION

 function changeName(event){
    setDeck({ ...deck, name: event.target.value })
  }

  function changeDescription(event){
    setDeck({ ...deck, description: event.target.value })
  }

  function handleCancel() {
    history.push(`/decks/${deck.id}`)
  }

  function handleSave (event) {
    event.preventDefault();
    updateDeck(deck).then((response) => history.push(`/decks/${deck.id}`))
  }


return(
    <div>
      <nav label="breadcrumb">
        <ol type="breadcrumb">
          <li type="breadcrumb-item"><a href="/">Home</a></li>
          <li type="breadcrumb-item"><a href={`/decks/${deckId}`}>{deck.name}</a></li>
          <li type="breadcrumb-item active" current="page">Edit Deck</li>
        </ol>
      </nav>
      <div>
        <form>
        <h1>Edit Deck</h1>
            <label> Name </label>
                <input
                type="text"
                id="front"
                onChange={changeName}
                value={deck.name}
                ></input>
            <label> Description </label>
                <textarea
                type="text"
                id="back"
                onChange={changeDescription}
                value={deck.description}
                >
                </textarea>

                    <button name="cancel" onClick={handleCancel}>Cancel</button>

                    <button name="submit" onClick={handleSave}>Save</button>
        </form>
      </div>
    </div>
)
}
